import React from 'react';
import styled from 'styled-components';

// Styled components
const PageContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
  color: white;
  background-color: #1a1a1a; 
  background-image: url('../assets/image1.jpg');
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center;
  text-align: center;
  padding: 20px;
  box-sizing: border-box;

  @media (max-width: 768px) {
    padding: 10px;
  }
`;

const Title = styled.h1`
  font-size: 2.5rem;
  margin-bottom: 0.5em;

  @media (max-width: 768px) {
    font-size: 1.5rem;
  }
`;

const Subtitle = styled.p`
  font-size: 1.2rem;
  line-height: 1.4;

  @media (max-width: 768px) {
    font-size: 1rem;
  }
`;

// React component
const CyberSecurityComponent = () => {
  return (
    <PageContainer>
      <Title>Web application attacks, Mobile hacks, lockpicking and more</Title>
      <Subtitle>Polish up your skills and show your technics about cyber security</Subtitle>
    </PageContainer>
  );
};

export default CyberSecurityComponent;
